mapper.select:
/* CommonMapper.selectCommonCode */
SELECT  common_group_cd
       ,common_cd
       ,code_order
       ,code_name
       ,code_desc
       ,protected_yn
  FROM common_code
 WHERE common_group_cd = #{commonGroupCd}
   AND common_cd       = #{commonCd}

mapper.insert:
/* CommonMapper.insertCommonCode */
INSERT INTO common_code (
		 common_group_cd
		,common_cd
		,code_order
		,code_name
		,code_desc
        ,protected_yn
		,reg_id
		,reg_name
		,reg_date
) 
SELECT  common_group_cd
       ,#{commonCd}  
       ,(MAX(code_order) + 1)
       ,#{codeName}  
       ,#{codeDesc}
       ,#{protectedYn}
	   ,#{regId}
	   ,#{regName}
	   ,NOW()
  FROM common_code
 WHERE common_group_cd = #{commonGroupCd}
 GROUP BY common_group_cd

/* CommonMapper.deleteCommonCode */
DELETE FROM common_code
 WHERE common_group_cd = #{commonGroupCd}
   AND common_cd       = #{commonCd}  
   AND protected_yn    = 'N'
